import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import xImg from '../../x.png'; 
import '../../App.css';

function WritePage() {
  const [ingredients, setIngredients] = useState(['']);
  const [steps, setSteps] = useState(['']);

  const addIngredient = () => setIngredients([...ingredients, '']);
  const addStep = () => setSteps([...steps, '']);

  return (
    <div className="container2">
      <div className='x-box'>
        <Link to='/'><img src={xImg} className='x-img' /></Link>
      </div>
      <div className='title-box-write'>
        <h1 className='ft-big'>님의 레시피를 소개해 주세요!</h1>
      </div>
      <div className="write-box">
        <h2 className="ft-small">제목을 작성해 주세요</h2>
        <input type="text" className="title-input" placeholder="제목을 작성해 주세요" />
        <div className="image-upload">
          <input type="file" />
        </div>
        <div className="info-box">
          <h3>난이도</h3>
          <div className="toggle-buttons">
            {[1, 2, 3, 4, 5].map(level => (
              <button key={level}>{level}</button>
            ))}
          </div>
          <h3>기준인분</h3>
          <div className="toggle-buttons">
            {['1인분', '2인분', '3인분', '4인분'].map(serving => (
              <button key={serving}>{serving}</button>
            ))}
          </div>
          <h3>가격</h3>
          <div className="toggle-buttons">
            {['5,000원 미만', '10,000원 미만', '20,000원 미만'].map(price => (
              <button key={price}>{price}</button>
            ))}
          </div>
          <h3>칼로리</h3>
          <input type="text" className="calorie-input" placeholder="칼로리 입력" />
        </div>
        <h3>재료</h3>
        {ingredients.map((_, index) => (
          <input key={index} type="text" className="ingredient-input" placeholder="재료 입력" />
        ))}
        <button onClick={addIngredient}>추가하기</button>
        <h3>조리순서</h3>
        {steps.map((_, index) => (
          <input key={index} type="text" className="step-input" placeholder="조리순서 입력" />
        ))}
        <button onClick={addStep}>추가하기</button>
        <button className="orange-btn">작성 완료</button>
      </div>
    </div>
  );
}

export default WritePage;
